mapper.select:
SELECT * FROM sys_menu WHERE deleted = 0
AND ( name LIKE CONCAT('%', #{keyword}, '%') OR title LIKE CONCAT('%', #{keyword}, '%') OR permission LIKE CONCAT('%', #{keyword}, '%') )
AND status = #{status}
AND type = #{type}
ORDER BY order_num ASC

SELECT * FROM sys_menu WHERE deleted = 0 AND parent_id = 0
AND status = #{status}
ORDER BY order_num ASC

SELECT COUNT(1)
FROM sys_menu
WHERE name = #{name} AND parent_id = #{parentId}
AND deleted = 0

    AND id != #{id}

mapper.update:
UPDATE sys_menu SET deleted = 1
WHERE id IN

    #{id}

UPDATE sys_menu
SET status = #{status}
WHERE id = #{id}
